import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA

class BtnNom extends React.Component {
	constructor(props,name){
		super(props);
		this.state={name:this.name};
	}

	render(){
		return(
			<div>
			<button class="btn_nom">{this.state.name}
			</button>
			</div>
		);

	}
}

class Boutons extends React.Component{
	constructor(props){
		super(props);
		this.state={BtnNom:Array(3).fill(this.name),
		};
	}

	render(){

		return (
		<div className="3_btn">
		<p className="fb">Facebook</p>
			<button class btn_nom> Jeanne </button>
			<button class btn_nom> Martine </button>
			<button class btn_nom> Claude </button>
		</div>
		);

	}

}

class FaceB extends React.Component {
	render() {
		return(
			<div className="navig">
			<Boutons />
			</div>
		);
	}
}
//
ReactDOM.render(
  <FaceB />,
  document.getElementById('root')
);
