import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA

class BtnNom extends React.Component {
	constructor(props){
		super(props);
		this.state={value:null,};
	}

	render(){
		return(
			<button class="btn_nom">{this.state.value}
			</button>
		);
	// passer d'une page à l'autre
	}
}

class Boutons extends React.Component{
	constructor(props){
		super(props);
		this.state={BtnNom:Array(3).fill(this.name),};
	}

	renderBtn(name){
		return <BtnNom value={name}/>;
	}

	render(){

		 return (
		<div className="3_btn">
			{this.renderBtn("Jeanne")}
			{this.renderBtn("Martine")}
			{this.renderBtn("Claude")}
		</div>
		);

	}

}
